DELETE FROM sqlite_sequence;
CREATE VIEW view_arteries AS SELECT * FROM 
(
WITH RECURSIVE tbl (
    id,
    name,
    parent_id,
    grouplevel,
    bottomgroupid
)
AS (
    SELECT bottomgroup.id,
           bottomgroup.name,
           bottomgroup.parent_id,
           0,
           bottomgroup.id
      FROM tbl_arteries bottomgroup
    UNION
    SELECT art.id,
           art.name,
           art.parent_id,
           tbl.grouplevel - 1,
           tbl.bottomgroupid
      FROM tbl_arteries art
           INNER JOIN
           tbl ON art.id = tbl.parent_id
)
SELECT *
  FROM tbl
 ORDER BY grouplevel) as grouptree;
CREATE VIEW view_grouptree AS SELECT * FROM 
(
WITH RECURSIVE tbl (
    id,
    name,
    lat_name,
    parent_id,
    grouplevel,
    bottomgroupid
)
AS (
    SELECT bottomgroup.id,
           bottomgroup.name,
           bottomgroup.lat_name,
           bottomgroup.parent_id,
           0,
           bottomgroup.id
      FROM tbl_musclegroups bottomgroup
    UNION
    SELECT mc.id,
           mc.name,
           mc.lat_name,
           mc.parent_id,
           tbl.grouplevel - 1,
           tbl.bottomgroupid
      FROM tbl_musclegroups mc
           INNER JOIN
           tbl ON mc.id = tbl.parent_id
)
SELECT *
  FROM tbl
 ORDER BY grouplevel) as grouptree;
CREATE VIEW view_grouptree_topdown AS SELECT * FROM 
(
WITH RECURSIVE tbl (
    id,
    name,
    lat_name,
    parent_id,
    grouplevel,
    topgroupid
)
AS (
    SELECT topgroup.id,
           topgroup.name,
           topgroup.lat_name,
           topgroup.parent_id,
           0,
           topgroup.id
      FROM tbl_musclegroups topgroup
    UNION
    SELECT mc.id,
           mc.name,
           mc.lat_name,
           mc.parent_id,
           tbl.grouplevel - 1,
           tbl.topgroupid
      FROM tbl_musclegroups mc
           INNER JOIN
           tbl ON mc.parent_id = tbl.id
)
SELECT *
  FROM tbl
 ORDER BY grouplevel) as grouptree;
